import numpy as np
import matplotlib.pyplot as plt

# Define the original function f(x)
def f(x):
    if x >= -3 and x <= 0:
        return -2 - x
    elif x >= 0 and x <= 2:
        return np.sqrt(4 - (x - 2)**2) - 2
    elif x >= 2 and x <= 3:
        return 2 * (x - 2)
    else:
        return None

# Define the transformed function f(x-1)
def f_shifted(x):
    return f(x - 1)

# Generate x values
x = np.linspace(-4, 5, 400)

