def can_be_made(n, coins):
    """ Check if n cents can be made using the given coins """
    for i in range(n // coins[0] + 1):
        for j in range(n // coins[1] + 1):
            for k in range(n // coins[2] + 1):
                if i * coins[0] + j * coins[1] + k * coins[2] == n:
                    return True
    return False

coins = [6, 10, 15]
max_value = 100  # We can set an arbitrary upper limit to find the largest value that cannot be made
