def can_be_expressed(n, coins):
    """Check if n can be expressed as a combination of coin values."""
    for i in range(n // coins[0] + 1):
        for j in range(n // coins[1] + 1):
            for k in range(n // coins[2] + 1):
                if i * coins[0] + j * coins[1] + k * coins[2] == n:
                    return True
    return False

coins = [6, 10, 15]
max_value = 100  # We will check up to 100 cents
for value in range(max_value, 0, -1):
    if not can_be_expressed(value, coins):
        result = value
        break

sum_of_digits = sum(int(digit) for digit in str(result))
print((result, sum_of_digits))
